// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

contract FrontRunningContract {
    address public leader;
    uint256 public highestBid;

    constructor() {
        leader = msg.sender;
        highestBid = 0;
    }

    function placeBid() external payable {
        require(msg.value > highestBid, "Bid must be higher than current highest bid");
        highestBid = msg.value;
        leader = msg.sender;
    }

    function withdrawBid() external {
        require(msg.sender == leader, "Only the leader can withdraw");
        payable(msg.sender).transfer(address(this).balance);
    }
}
contract FrontRunningAttacker {
    FrontRunningContract public targetContract;

    constructor(address _targetContract) {
        targetContract = FrontRunningContract(_targetContract);
    }

function attack() external payable {
    // Place a bid with a fixed higher value than the current highest bid
    uint256 bidValue = targetContract.highestBid() + 10 ether; // Sending 10 ether higher than the current highest bid
    targetContract.placeBid{value: bidValue}();
}


}
