// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;
import "@openzeppelin/contracts/access/Ownable.sol";
contract NoAccessControlSecure is Ownable {
    mapping(address => uint256) private amount;
    uint256 private maxWithdrawal = 5 ether;
    event EtherDeposited(address indexed depositor, uint256 amount);
    event EtherWithdrawn(address indexed recipient, uint256 amount);
    event WithdrawalLimitModification(uint256 newLimit);
    constructor() Ownable(msg.sender) {}
    modifier onlyAuthorized(address account) {
        require(msg.sender == owner() || msg.sender == account, "access is not authorised");
        _;
    }
    function deposit() public payable onlyAuthorized(msg.sender) {
        amount[msg.sender] += msg.value;
        emit EtherDeposited(msg.sender, msg.value);
    }
   function withdraw() public onlyAuthorized(msg.sender) {
        uint256 balance = amount[msg.sender];
        require(balance > 0, "No ether deposited");
        require(balance <= maxWithdrawal, "Withdrawal amount exceeds limit");
        amount[msg.sender] = 0;
        payable(msg.sender).transfer(balance);
        emit EtherWithdrawn(msg.sender, balance);
    }
    function setWithdrawalLimit(uint256 newLimit) public onlyOwner {
        maxWithdrawal = newLimit;
        emit WithdrawalLimitModification(newLimit);
    }
    function getBalance(address addr) public view returns (uint256) {
        return amount[addr];
    }
}
