// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SimpleDEX is Ownable {
    IERC20 public myToken;
    mapping(address => uint256) public balances;

    event Deposit(address indexed account, uint256 amount);
    event Withdraw(address indexed account, uint256 amount);

    // Additional event for ReentrantContract
    event ReentrantCall(address indexed account, uint256 amount);

    constructor(address _myToken, address _owner) Ownable(_owner) {
        myToken = IERC20(_myToken);
    }

    function deposit() external payable {
        require(myToken.transferFrom(msg.sender, address(this), msg.value), "Transfer failed");
        balances[msg.sender] += msg.value;
        emit Deposit(msg.sender, msg.value);

        // Introduce an unchecked external call vulnerability by calling an external contract
        // This is for educational purposes only; avoid using such patterns in production.
        ReentrantContract(msg.sender).callDeposit(msg.value);

        // Emit an event to indicate the reentrant call
        emit ReentrantCall(msg.sender, msg.value);
    }

    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        myToken.transfer(msg.sender, amount);
        emit Withdraw(msg.sender, amount);
    }
}

// Additional contract for ReentrantContract
contract ReentrantContract {
    IERC20 public myToken;

    constructor(address _myToken) {
        myToken = IERC20(_myToken);
    }

    function callDeposit(uint256 amount) external {
        // Simulate some logic in the external contract that may reenter
        // For example, let's reenter the calling contract's deposit function
        SimpleDEX(msg.sender).deposit{value: amount}();

        // Perform other logic if needed (this part will execute after the reentrancy)
        // ...
    }
}
