// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract VulnerableDefiMart {
    address public owner;
    mapping(address => uint256) public stakedBalance;
    IERC20 public rewardToken;

    constructor(address _rewardToken) {
        owner = msg.sender;
        rewardToken = IERC20(_rewardToken);
    }

    function stake(uint256 _amount) external {
        // Vulnerability: Integer Overflow/Underflow
        stakedBalance[msg.sender] += _amount;
        rewardToken.transferFrom(msg.sender, address(this), _amount);
    }

    function withdraw() external {
        // Vulnerability: Reentrancy Attack
        uint256 amount = stakedBalance[msg.sender];
        stakedBalance[msg.sender] = 0;
        rewardToken.transfer(msg.sender, amount);
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");
    }

    function updateOwner(address _newOwner) external {
        // Vulnerability: Access Control Vulnerability
        require(msg.sender == owner, "Not the owner");
        owner = _newOwner;
    }

    function insecureRandom() external view returns (uint256) {
        // Vulnerability: Insecure Randomness
        return block.timestamp;
    }

    function executeExternalCall(address _target, bytes memory _data) external {
        // Vulnerability: Unchecked External Calls
        (bool success, ) = _target.call(_data);
        require(success, "External call failed");
    }
}
